import React, { useEffect, useState } from "react";
import QRCode from "react-qr-code";
import io from "socket.io-client";

const socket = io("http://localhost:3000"); // Change the server URL to match your server's URL

function App() {
  const [qrCode, setQRCode] = useState("");
  console.log(qrCode);

  useEffect(() => {
    // Listen for the 'qr' event from the server
    socket.on("qr", (qrData) => {
      setQRCode(qrData);
    });

    return () => {
      // Clean up when the component unmounts
      socket.disconnect();
    };
  }, []);

  return (
    <div className="App">
      <h1>WhatsApp Web Js Client</h1>
      <h2>Open WhatsApp and Scan QR mentioned Below</h2>
      <QRCode value={qrCode} />
    </div>
  );
}

export default App;
