import React, { useEffect, useState } from "react";
import QRCode from "react-qr-code";
import io from "socket.io-client";

const socket = io("http://localhost:3002");

function App() {
  const [qrCode, setQRCode] = useState("");
  const [session, setSession] = useState("");
  const [id, setId] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login status

  const createWhatsappSession = () => {
    socket.emit("createSession", {
      id: session,
    });
  };

  const getAllChats = () => {
    socket.emit("getAllChats", { id });
  };

  useEffect(() => {
    socket.on("qr", (qrData) => {
      console.log(qrData);
      setQRCode(qrData);
    });

    socket.on("ready", (data) => {
      console.log("ready", data);
      const { id } = data;
      setId(id);
      setIsLoggedIn(true); // User is logged in when "ready" event is received
    });

    socket.on("allChats", (data) => {
      console.log("allChats", data);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  return (
    <div className="App" style={{ margin: "40px" }}>
      <h1>WhatsApp Web Js Client</h1>
      <h2>Open WhatsApp and Scan QR mentioned Below</h2>
      <div style={{ marginBottom: "10px" }}>
        <input
          type="text"
          value={session}
          onChange={(val) => setSession(val.target.value)}
        />
        <button onClick={createWhatsappSession}>Create Session</button>
      </div>
      <div style={{ marginBottom: "25px" }}>
        {isLoggedIn &&
          id !== "" && ( // Display button only when logged in
            <div>
              <button onClick={getAllChats}>Get All chats</button>
              <QRCode value={qrCode} />
            </div>
          )}
      </div>
    </div>
  );
}

export default App;
