import "./App.css";
import QRCode from "react-qr-code";
import io from "socket.io-client";
import { useEffect, useState } from "react";
const socket = io.connect("http://localhost:3000", {});
function App() {
  const [session, setSession] = useState("");
  const [qr, setQr] = useState("");
  const createSession = () => {
    socket.emit("createSession", {
      id: session,
    });
  };
  useEffect(() => {
    socket.emit("connected", "hello from client");
    socket.on("qr", (qr) => {
      console.log("QR RECIEVED", qr);
    });
  }, []);
  return (
    <div className="App">
      <h1>WhatsApp Web Js Client</h1>
      <h2>Open Whatsapp and Scan QR mentioned Below</h2>
      <div style={{ margin: "30px" }}>
        <input
          type="text"
          value={session}
          onChange={(val) => {
            setSession(val.target.value);
          }}
        />
        <button onClick={createSession}>Create Session</button>
      </div>
      <QRCode value="2@gAFYEU20WcBmhXNVsf0nCH3hIMmdKAiEssE87ENB0i6iPLXbWODYvC3HmMOM7P/iuX6E7wnfWwzXNA==,xqZRMrgQKqvb2iSIDSJRxMnMMMR7nDGRrTXz0xnA2Ws=,54tmLSWgdO7MW67ckS4Z/WP5djMxCeLsdNIKJSUTanQ=,Pc6T43sk0Sl3ebIqSVXlrnyrP3x/xHRIVHNXpENgg/Y=,1" />
    </div>
  );
}

export default App;
