import React, { useEffect, useState } from "react";
import QRCode from "react-qr-code";
import io from "socket.io-client";

const socket = io("http://localhost:3002");

function App() {
  const [qrCode, setQRCode] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Listen for session-related events from the server
    socket.on("loggedIn", () => {
      setIsLoggedIn(true);
    });

    socket.on("loggedOut", () => {
      setIsLoggedIn(false);
    });

    // Send a "checkSession" request to the server to determine the initial session status
    socket.emit("checkSession");

    // Listen for the QR code event
    socket.on("qr", (qrData) => {
      console.log(qrData);
      setQRCode(qrData);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleLogin = () => {
    // Simulate a login action
    socket.emit("login");
  };

  const handleLogout = () => {
    // Simulate a logout action
    socket.emit("logout");
  };

  return (
    <div className="App">
      <h1>WhatsApp Web Js Client</h1>
      {isLoggedIn ? (
        <div>
          <h2>You are logged in.</h2>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <div>
          <h2>Open WhatsApp and Scan QR mentioned Below</h2>
          <QRCode value={qrCode} />
          <button onClick={handleLogin}>Login</button>
        </div>
      )}
    </div>
  );
}

export default App;
